  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent implements OnInit {

  @ViewChild('particpant_name') name!: ElementRef;

  constructor() { }

  ngOnInit(): void {
  }

  startQuiz(){
    localStorage.setItem("particpant_name",this.name.nativeElement.value)
  }

}
